// Type declaration for plotly.js-dist-min (minimal build of plotly.js)
// The runtime module exists but has no bundled type definitions.
// plotly.js-dist-min exposes the same API as plotly.js, just smaller.
declare module 'plotly.js-dist-min' {
  import * as Plotly from 'plotly.js'
  export = Plotly
}
